<?php

namespace ProjectBundle\Repository;
use UserBundle\Entity\User;
use ProjectBundle\Entity\Project;
use BeneficiaryBundle\Entity\Household;

/**
 * ProjectRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ProjectRepository extends \Doctrine\ORM\EntityRepository
{

    public function getAllOfUser(User $user)
    {
        $qb = $this->createQueryBuilder("p");
        $q = $qb->leftJoin("p.usersProject", "up")
            ->where("up.user = :user")
            ->andWhere("p.archived = 0")
            ->setParameter("user", $user);

        return $q->getQuery()->getResult();
    }

    public function getAllOfCountry($iso3)
    {
        $qb = $this->createQueryBuilder("p");
        $q = $qb->where("p.iso3 = :iso3")
            ->andWhere("p.archived = 0")
            ->setParameter("iso3", $iso3);

        return $q->getQuery()->getResult();
    }

    /**
     * Get project if has household
     *
     * @param string $iso3
     * @param Household $household
     * @param Project $project
     * @return Project
     */
    public function getIfHasHousehold(string $iso3, Household $household, Project $project)
    {
        $qb = $this->createQueryBuilder("p");
        $q = $qb->where("p.iso3 = :iso3")
                    ->setParameter("iso3", $iso3)
                ->andWhere("p = :project")
                    ->setParameter("project", $project)
                ->leftJoin("p.households", "hh")
                ->andWhere("hh = :hh")
                ->setParameter("hh", $household);

        return $q->getQuery()->getOneOrNullResult();
    }

}
